package November13;

public class whileDowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  int i=10;  
		  //Entry controlled loop
		    while(i>=2){  
		        System.out.print(i);  
		    i--;  
		    }
		    
		    
		    //Exit controlled loop
		    int j=0;
		    do {
		 		System.out.print(j);
		 		j++;
		 	}	while(j<10);
		
		
	}

}
